mapper.select:
SELECT n.*, (SELECT MEMBER_NICKNAME FROM member m WHERE m.MEMBER_ID = n.MEMBER_ID) name 
FROM notice n 
WHERE id=#{id }

mapper.update:
UPDATE notice 
SET VIEWS = VIEWS + 1 
WHERE id=#{id}

mapper.insert:


{CALL DECLARE BEGIN
	UPDATE notice SET step = step + 1, dept = dept + 1
	WHERE root = #{root } AND step > #{step } AND dept > #{dept};

	INSERT INTO notice (id, root, title, content , MEMBER_ID, step, dept, filename, filepath)
	VALUES (NT_SEQ.NEXTVAL, #{root }, #{title }, #{content }, #{MEMBER_ID }, #{step } + 1, #{dept } + 1, #{filename, jdbcType=VARCHAR }, #{filepath, jdbcType=VARCHAR });
END}
